#bmanNos[]#
GROUP BY VENDOR_ID

/* SQLINES DEMO *** ON_CP_SQL01_OLD */ SELECT VENDOR_ID AS CODE_ID ,VENDOR_ID AS CODE_NAME FROM TVE_VENDOR
#bmanNos[]#
GROUP BY VENDOR_ID

/* SQLINES DEMO *** eamList*/ SELECT DISTINCT TEAM_CD , TEAM_NM AS ORG_NM FROM TPC_L1_CD
WHERE TEAM_CD = #detailCode#
ORDER BY TEAM_CD

/* SQLINES DEMO *** eamListOld*/ SELECT DISTINCT TEAM_CD, ORG_NM FROM TED_L1_CD
WHERE TEAM_CD = #detailCode#
ORDER BY TEAM_CD

/* SQLINES DEMO *** 1List*/ SELECT DISTINCT L1_CD , L1_NM AS CAT_NM FROM TPC_L1_CD WHERE TEAM_CD = #groupCode#
L1_CD = #detailCode#
ORDER BY L1_CD

/* SQLINES DEMO *** 1ListOld*/ SELECT DISTINCT L1_CD, CAT_NM FROM TED_L1_CD WHERE TEAM_CD = #groupCode#
L1_CD = #detailCode#
ORDER BY L1_CD

/* SQLINES DEMO *** lineDisplayCategoryList */ WITH RECURSIVE category_tree AS ( -- 최상위(root) 카테고리부터 시작 SELECT CATEGORY_ID, UP_CATEGORY_ID, CATEGORY_NM, CATEGORY_TYPE_CD, DEPTH, STDIST_EXUSE_YN, DISP_YN, START_DATE, END_DATE, ORDER_SEQ, CATEGORY_NM AS FULL_CATEGORY_NM, 1 AS LVL FROM TCA_CATEGORY WHERE UP_CATEGORY_ID = 'root'
UNION ALL
-- 자식 카테고리를 재귀적으로 연결 SELECT c.CATEGORY_ID, c.UP_CATEGORY_ID, c.CATEGORY_NM, c.CATEGORY_TYPE_CD, c.DEPTH, c.STDIST_EXUSE_YN, c.DISP_YN, c.START_DATE, c.END_DATE, c.ORDER_SEQ, parent.FULL_CATEGORY_NM || '/' || CASE WHEN c.CATEGORY_NM = '유아/아동/완구' THEN '유아/아동' ELSE c.CATEGORY_NM END AS FULL_CATEGORY_NM, parent.LVL + 1 FROM TCA_CATEGORY c INNER JOIN category_tree parent ON c.UP_CATEGORY_ID = parent.CATEGORY_ID ), filtered_category AS ( SELECT ct.*, TO_CHAR(TO_DATE(START_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS START_DATE_FMT, TO_CHAR(TO_DATE(END_DATE, 'YYYYMMDD'), 'YYYY-MM-DD') AS END_DATE_FMT FROM category_tree ct WHERE ct.CATEGORY_TYPE_CD IN ('01', '11') AND ct.DISP_YN = 'Y' AND NOW() BETWEEN TO_TIMESTAMP(ct.START_DATE || '000000', 'YYYYMMDDHH24MISS') AND TO_TIMESTAMP(ct.END_DATE || '000000', 'YYYYMMDDHH24MISS') ) SELECT ROW_NUMBER() OVER () AS num, fc.CATEGORY_ID, fc.FULL_CATEGORY_NM, CASE WHEN fc.CATEGORY_NM = '유아/아동/완구' THEN '유아/아동' ELSE fc.CATEGORY_NM END AS CATEGORY_NM, fc.CATEGORY_TYPE_CD, ec.CD_NM AS CATEGORY_TYPE_NM, fc.DEPTH, fc.STDIST_EXUSE_YN, fc.DISP_YN, fc.START_DATE_FMT AS START_DATE, fc.END_DATE_FMT AS END_DATE FROM filtered_category fc JOIN TET_CODE ec ON fc.CATEGORY_TYPE_CD = ec.MINOR_CD WHERE ec.MAJOR_CD = 'SM007' AND fc.CATEGORY_ID NOT LIKE 'C0010005%' AND fc.CATEGORY_ID NOT LIKE 'C00100030006%' AND fc.CATEGORY_ID NOT LIKE 'C00100030007%' AND fc.CATEGORY_ID NOT LIKE 'C00100030008%' AND fc.CATEGORY_ID NOT LIKE 'C00100030009%' AND fc.CATEGORY_ID NOT LIKE 'C00100030010%' AND fc.CATEGORY_ID NOT LIKE 'C00100030011%' AND fc.CATEGORY_ID LIKE CONCAT(#categoyDepth2#::varchar , '%')

/* common.getProdAddTemplateDetailList */ SELECT (select MST.INFO_GRP_NM from TPR_PROD_ADD_INFO_MST MST where MST.INFO_GRP_CD=DET.INFO_GRP_CD LIMIT 1) AS org_nm2 ,DET.INFO_GRP_CD AS org_cd2 ,DET.INFO_COL_CD AS org_cd ,DET.INFO_COL_NM AS org_nm ,DET.INFO_COL_DESC AS org_desc ,DET.ORDER_SEQ FROM TPR_PROD_ADD_INFO_DET DET WHERE 1=1 AND DET.USE_YN='Y'
AND DET.INFO_GRP_CD = #infoGrpCd#
ORDER BY DET.INFO_GRP_CD ASC ,DET.ORDER_SEQ ASC

/* common.getProdCertTemplateDetailList */ SELECT (select MST.INFO_GRP_NM from TPR_PROD_ADD_INFO_MST MST where MST.INFO_GRP_CD=DET.INFO_GRP_CD LIMIT 1) AS org_nm2 ,DET.INFO_GRP_CD AS org_cd2 ,DET.INFO_COL_CD AS org_cd ,DET.INFO_COL_NM AS org_nm ,DET.INFO_COL_DESC AS org_desc ,DET.ORDER_SEQ FROM TPR_PROD_CERT_INFO_DET DET WHERE 1=1 AND DET.USE_YN='Y'
AND DET.INFO_GRP_CD = #infoGrpCd#
ORDER BY DET.INFO_GRP_CD ASC ,DET.ORDER_SEQ ASC

/* common.getProdAddTemplateUpdateDetailList */ SELECT DET.INFO_GRP_CD AS org_cd2 ,DET.INFO_COL_CD AS org_cd ,DET.INFO_COL_NM AS org_nm ,DET.INFO_COL_DESC AS org_desc ,VAL.COL_VAL AS col_val FROM TPR_PROD_ADD_INFO_DET DET, TPR_PROD_ADD_INFO_VAL VAL WHERE DET.INFO_GRP_CD = VAL.INFO_GRP_CD(+) AND DET.INFO_COL_CD = VAL.INFO_COL_CD(+) AND DET.USE_YN = 'Y' AND CURRENT_TIMESTAMP(0) BETWEEN TO_DATE(DET.VALI_START_DATE,'YYYYMMDDHH24MISS') AND TO_DATE(DET.VALI_END_DATE,'YYYYMMDDHH24MISS')
AND DET.INFO_GRP_CD = #infoGrpCd# AND VAL.NEW_PROD_CD(+) = #newProductCode#
ORDER BY DET.ORDER_SEQ

/* common.getProdCertTemplateUpdateDetailList */ SELECT DET.INFO_GRP_CD AS org_cd2 ,DET.INFO_COL_CD AS org_cd ,DET.INFO_COL_NM AS org_nm ,DET.INFO_COL_DESC AS org_desc ,VAL.COL_VAL AS col_val FROM TPR_PROD_CERT_INFO_DET DET, TPR_PROD_CERT_INFO_VAL VAL WHERE DET.INFO_GRP_CD = VAL.INFO_GRP_CD(+) AND DET.INFO_COL_CD = VAL.INFO_COL_CD(+) AND DET.USE_YN = 'Y' AND CURRENT_TIMESTAMP(0) BETWEEN TO_DATE(DET.VALI_START_DATE,'YYYYMMDDHH24MISS') AND TO_DATE(DET.VALI_END_DATE,'YYYYMMDDHH24MISS')
AND DET.INFO_GRP_CD = #infoGrpCd# AND VAL.NEW_PROD_CD(+) = #newProductCode#
ORDER BY DET.ORDER_SEQ

/* SQLINES DEMO *** CODE_SQL01 */ SELECT DETAIL_CD AS CODE_ID , DETAIL_NM AS CODE_NAME FROM ALL_CD@DL_MD_MARTNIS WHERE GRP_CD = #parentCodeId#
AND DETAIL_VAL1 = #subCodeId#
ORDER BY DETAIL_CD
