sqlMap.select:
/* SQLINES DEMO *** ectNewTmpProductImgInfo */ SELECT A.PGM_ID AS PGM_ID /* 프로그램ID */ , A.VARIANT AS VARIANT /* 변형순번 */ , '1' AS SEQ /* 순번 */ , B.IMG_NM AS ZZIMAGE /* 이미지 이름 */ , 'I' AS CFM_FG /* 수정구분자 */ FROM ( SELECT A.PGM_ID , CASE WHEN B.VARIANT IS NULL THEN '000' ELSE B.VARIANT END AS VARIANT FROM TPC_NEW_PROD_REG A LEFT OUTER JOIN TPC_VAR_ATT B ON A.PGM_ID= B.PGM_ID
#arrPgmId[]#
AND A.ONOFF_DIVN_CD = '0' /* 온오프 상품만 */ GROUP BY A.PGM_ID, B.VARIANT ORDER BY A.PGM_ID DESC ) A LEFT OUTER JOIN TPC_SALE_IMG B ON A.PGM_ID = B.PGM_ID

sqlMap.update:
/* SQLINES DEMO *** ateImsiProductList */ UPDATE ( SELECT PGM_ID , CASE WHEN ONOFF_DIVN_CD = '0' AND TRD_TYPE_DIVN_CD = '1' AND MAT_CD = '2' AND VIC_ONLINE_CD IS NULL THEN 'S' /* SQLINES DEMO *** 직매입일 ,VIC마켓전용,온라인미적용 이면 경우 'S' */ WHEN ONOFF_DIVN_CD = '0' AND TRD_TYPE_DIVN_CD = '1' AND MAT_CD = '2' AND VIC_ONLINE_CD ='X' THEN 'W' /* SQLINES DEMO *** 직매입일 ,VIC마켓전용,온라인적용 이면 경우 'W' */ WHEN ONOFF_DIVN_CD = '0' AND TRD_TYPE_DIVN_CD = '1' AND ADM_FG = '3' THEN 'W' /* SQLINES DEMO *** 직매입일,관리자일때는 'W' */ WHEN ONOFF_DIVN_CD = '0' AND TRD_TYPE_DIVN_CD = '1' THEN 'A' /* SQLINES DEMO *** 직매입일경우 'A' */ ELSE 'S' END SEND_GBN , MD_SEND_DIVN_CD FROM TPC_NEW_PROD_REG
#arrPgmId[]#
1 = 0
) MD_SEND_DIVN_CD := SEND_GBN

/* SQLINES DEMO *** ateImsiProductListAdm */ UPDATE ( SELECT PGM_ID , CASE WHEN ONOFF_DIVN_CD = '0' AND TRD_TYPE_DIVN_CD = '1' AND ADM_FG='3' THEN 'W' /* SQLINES DEMO *** 직매입일경우 'A' 아닐경우 'S' */ WHEN ONOFF_DIVN_CD = '0' AND TRD_TYPE_DIVN_CD = '1' THEN 'A' /* SQLINES DEMO *** 직매입일경우 'A' 아닐경우 'S' */ ELSE 'S' END SEND_GBN , MD_SEND_DIVN_CD FROM TPC_NEW_PROD_REG WHERE 1 = 1
