) b ON (A.PGM_ID = b.pgm_id) WHEN NOT MATCHED THEN INSERT ( PGM_ID ,PRGS_STS ,REG_DATE ,REG_ID ,MOD_DATE ,MOD_ID ,OFF_CFM_DT ) VALUES ( b.pgm_id , '80' /* 진행상태(PRD32) */ , CURRENT_TIMESTAMP(0) /* 등록일시 */ , '000000000001' /* 등록자 */ , CURRENT_TIMESTAMP(0) /* 수정일시 */ , '000000000001' /* 수정자 */ , CURRENT_TIMESTAMP(0) ) WHEN MATCHED THEN UPDATE SET PRGS_STS = '80' , REG_DATE = CURRENT_TIMESTAMP(0) , REG_ID = '000000000001' , MOD_DATE = CURRENT_TIMESTAMP(0) , MOD_ID ='000000000001' , OFF_CFM_DT = CURRENT_TIMESTAMP(0)

