sqlMap.select:
/* SQLINES DEMO *** ectImsiProdClassToLogiBcdInfo */ SELECT A.PGM_ID /* 프로그램 ID */ , A.SEQ /* 순번 */ , A.LOGI_BCD /* 물류바코드 */ , A.VARIANT /* 변형구분 */ , A.MIX_PROD_FG /* 혼재여부 */ , A.LOGI_BOX_IPSU /* */ , A.WG /* 총중량 */ , A.WIDTH /* 가로 */ , A.LENGTH /* 세로 */ , A.HEIGHT /* 높이 */ , A.SIZE_UNIT /* 사이즈단위 */ , A.WG_UNIT /* 무게단위 */ , A.CONVEY_FG /* 소터에러 구분 */ , A.SORTER_FG /* 소터구분 */ , A.INNER_IPSU /* 가로박스 */ , A.PLT_LAYER_QTY /* 세로박스 */ , A.PLT_HEIGHT_QTY /* 높이박스 수 */ , A.CRSDK_FG /* 크로스독구분 */ , A.W_USE_FG /* 빅마켓 취급구분 */ , A.W_INNER_IPSU /* 빅마켓 박스수 */ , A.USE_FG /* 사용구분 */ , A.PROD_CD /* 상품코드 */ , A.L1_CD /* 대분류코드 */ , A.VEN_CD /* 협력업체코드 */ , TO_CHAR(A.REG_DATE, 'YYYY-MM-DD') AS REG_DATE /* 등록일시 */ , A.REG_ID /* 등록자 */ , A.MOD_DATE /* 수정일시 */ , A.MOD_ID /* 수정자 */ , CASE WHEN NULLIF(B.LOGI_CFM_FG,'') IS NULL THEN '02' ELSE B.LOGI_CFM_FG END AS LOGI_CFM_FG /* SQLINES DEMO *** 9:완료] */ , A.INNER_IPSU * A.PLT_LAYER_QTY * A.PLT_HEIGHT_QTY AS TOTAL_BOX /* 총 박스수 */ FROM TPC_LOGI_BCD A LEFT OUTER JOIN TPC_LOGI_BCD_SAP B ON A.PGM_ID = B.PGM_ID WHERE A.PGM_ID = #pgmId# AND A.VEN_CD = #venCd#
AND A.SEQ = #seq#
AND A.VARIANT = #variant#

/* NEDMPRO0220.selectBarcodeList */ SELECT B.* FROM PRODUCT A , ( SELECT -- SQLINES DEMO *** 준 인덱스 사용. COALESCE(B.LOGI_CFM_FG, '02') AS LOGI_CFM_FG , CASE WHEN NULLIF(B.SRCMK_CD,'') IS NULL THEN A.SRCMK_CD ELSE B.SRCMK_CD END AS SRCMK_CD , A.LOGI_BCD AS LOGI_BCD , A.LOGI_BOX_IPSU AS LOGI_BOX_IPSU , TO_CHAR(A.REG_DATE,'YYYY-MM-DD') AS REG_DT , A.USE_FG AS USE_FG , A.WIDTH AS WIDTH , A.LENGTH AS LENGTH , A.HEIGHT AS HEIGHT , A.WG AS WG , A.MIX_PROD_FG AS MIX_PROD_FG , A.CONVEY_FG AS CONVEY_FG , A.SORTER_FG AS SORTER_FG , A.INNER_IPSU AS INNER_IPSU , A.PLT_LAYER_QTY AS PLT_LAYER_QTY , A.PLT_HEIGHT_QTY AS PLT_HEIGHT_QTY , A.INNER_IPSU * A.PLT_LAYER_QTY * A.PLT_HEIGHT_QTY AS TOTAL_BOX , A.SEQ AS SEQ , A.PGM_ID , A.CRSDK_FG , A.W_USE_FG , A.W_INNER_IPSU , A.SIZE_UNIT , ( SELECT CD_NM AS CODE_NAME FROM TPC_CODE WHERE MAJOR_CD = 'PRD42' AND MINOR_CD = A.SIZE_UNIT ) AS SIZE_UNIT_NAME , A.WG_UNIT , ( SELECT CD_NM AS CODE_NAME FROM TPC_CODE WHERE MAJOR_CD = 'PRD41' AND MINOR_CD = A.WG_UNIT ) AS WG_UNIT_NAME , A.REG_DATE FROM TPC_LOGI_BCD A LEFT OUTER JOIN TPC_LOGI_BCD_SAP B ON A.PGM_ID = B.PGM_ID AND A.SEQ = B.SEQ AND A.LOGI_BCD = B.LOGI_BCD WHERE A.VEN_CD = #searchVenCd# --'04... SQLINES DEMO ***
) B WHERE A.SRCMK_CD = B.SRCMK_CD AND A.VEN_CD = #searchVenCd# --'04... SQLINES DEMO *** AND A.SRCMK_CD = #searchSrcmkCd# --'88... SQLINES DEMO ***
AND B.PGM_ID NOT IN ( SELECT BB.PGM_ID FROM TPC_NEW_PROD_REG AA LEFT OUTER JOIN TPC_NEW_PROD_REG_SAP BB ON AA.PGM_ID = BB.PGM_ID WHERE AA.PGM_ID = B.PGM_ID AND (BB.CFM_FG <> '3' OR (BB.CFM_FG IS NULL OR BB.CFM_FG = '')) ) ORDER BY B.REG_DT DESC
